import React, { useState, useEffect } from "react";
import ExpenseForm from "./Components/ExpenseForm/ExpenseForm";
import ExpenseInfo from "./Components/ExpenseInfo/ExpenseInfo";
import ExpenseList from "./Components/ExpenseList/ExpenseList";
import "./App.css";

function App() {
    useEffect(() => {
        document.title = "Expense Tracker";
    }, []);

    const [expenses, setExpenses] = useState([]);
    const [item, setItem] = useState("");
    const [amount, setAmount] = useState("");

    const addExpense = (expense) => {
        setExpenses((prevState) => [expense, ...prevState]);
    };

    const deleteExpense = (id) => {
        setExpenses((prevExpenses) => {
            return prevExpenses.filter((expense) => expense.id !== id);
        });
    };

    const changeExpenseToUpdate = (expense) => {
        // Update the states controlling the form with the values of
        // the selected expense
        setItem(expense.item);
        setAmount(expense.amount);

        // Delete the selected expense from the list
        setExpenses((prevExpenses) => {
            return prevExpenses.filter((prevExpense) => prevExpense.id !== expense.id);
        });
    };

    return (
        <>
            <h2 className="heading">Expense Tracker</h2>
            <div className="App">
                <ExpenseForm addExpense={addExpense} />
                <div className="expense">
                    <ExpenseInfo expenses={expenses} />
                    <ExpenseList
                        expenses={expenses}
                        deleteExpense={deleteExpense}
                        changeExpenseToUpdate={changeExpenseToUpdate}
                    />
                </div>
            </div>
        </>
    );
}

export default App;
