import React, { useState } from "react";
import styles from "./Transactions.module.css";
import EditImage from '/src/images/edit.png';
import RemoveImage from '/src/images/trash-bin.png';

const Transactions = ({ expense,deleteExpense, index }) => {
    const [currentHoverIndex, setCurrentHoverIndex] = useState(null);

    return (
        <li
            key={expense.id}
            className={`${styles.transaction} ${expense.amount > 0 ? styles.profit : styles.loss}`}
            onMouseOver={() => {
                setCurrentHoverIndex(index);
            }}
            onMouseLeave={() => {
                setCurrentHoverIndex(null);
            }}
        >
            <div>{expense.item}</div>
            <div className={styles.transactionOptions}>
                <div
                    className={`${styles.amount} ${currentHoverIndex === index && styles.movePrice
                        }`}
                >
                    ${expense.amount}
                </div>
                <div
                    className={`${styles.btnContainer} ${currentHoverIndex === index && styles.active
                        }`}
                >
                    <div className={styles.edit} onClick={() => { }}>
                        <img src={EditImage} height="100%" alt="Edit" />
                    </div>
                    <div
                        className={styles.delete}
                        onClick={() => deleteExpense(expense.id)}
                    >
                        <img src={RemoveImage} height="100%" alt="Delete" />
                    </div>
                </div>
            </div>
        </li>
    );
};

export default Transactions;
