import React,{useState, useEffect} from 'react';
import ExpenseForm from './Components/ExpenseForm/ExpenseForm';
import ExpenseInfo from './Components/ExpenseInfo/ExpenseInfo';
import ExpenseList from './Components/ExpenseList/ExpenseList';
import "./App.css";

function App() {

  useEffect(()=>{
    document.title="Expense Tracker";
  },[]);

  const[expenses, setExpenses]=useState([]);

  const addExpense=(expense)=>{
    setExpenses((prevState)=>[expense, ...prevState]);
  };

  const deleteExpense = (id) => {
    setExpenses((prevExpenses) => {
      return prevExpenses.filter((expense) => expense.id !== id);
    });
  };

  const resetExpenseToUpdate = () => {
    setExpenseToUpdate(null);
  };

  const updateExpense = (expense, expenses, setExpenses) => {
  // Find the index of the expense to update
  const expenseIndex = expenses.findIndex((exp) => exp.id === expense.id);

  if (expenseIndex === -1) {
    return false; // Expense not found
  }

  // Create a new array of expenses with the updated expense
  const updatedExpenses = [...expenses];
  updatedExpenses[expenseIndex] = expense; 

  // Update the state with the new array of expenses
  setExpenses(updatedExpenses); 

  return true; 
};


  return (
    <>
      <h2 className="heading">Expense Tracker</h2>
      <div className="App">
        <ExpenseForm addExpense={addExpense}
                     expenseToUpdate={expenseToUpdate}
                     updateExpense={updateExpense}
                     resetExpenseToUpdate={resetExpenseToUpdate}/>
        <div className="expense">
          <ExpenseInfo expenses={expenses}/>
          <ExpenseList expenses={expenses} 
                       deleteExpense={deleteExpense}
                       changeExpenseToUpdate={setExpenseToUpdate}/>
        </div>
      </div>
    </>
  );
};

export default App;
