import React, { useEffect, useState } from "react";
import styles from "./ExpenseForm.module.css"
import { db } from "../../FirebaseInit";

import { collection, addDoc, getDocs, onSnapshot } from "firebase/firestore";



const ExpenseForm = ({ addExpense,
  expenseToUpdate,
  updateExpense,
  resetExpenseToUpdate }) => {

  const [item, setItem] = useState("")
  const [amount, setAmount] = useState("")

  useEffect(() => {
    if (!expenseToUpdate) return;
    setItem(expenseToUpdate.item);
    setAmount(expenseToUpdate.amount);
  }, [expenseToUpdate]);

  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, "expenses"), (snapShot) => {
      const expenses = snapShot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));

      addExpense(expenses);  // ✅ Only update state once
    });

    return () => unsubscribe();  // ✅ Cleanup listener when component unmounts
  }, []);


  async function handleSubmit(e) {
    e.preventDefault();

    if (parseInt(amount) === 0) {
      return;
    }

    const docRef = await addDoc(collection(db, "expenses"), {
      item,
      amount,
      createdOn: new Date()
    });

    if (!expenseToUpdate) {
      const expense = {
        item,
        amount,
        id: new Date().getTime()
      };
      addExpense(expense);
      clearInput();
      return;
    }

    const expense = {
      item,
      amount,
      id: expenseToUpdate.id
    };

    const result = updateExpense(expense);
    if (!result) return;
    clearInput();
    resetExpenseToUpdate();

  };

  const clearInput = () => {
    setItem("");
    setAmount("");
  };

  return (

    <form className={styles.form} onSubmit={handleSubmit}>

      <h3>Add Transaction</h3>

      <label htmlFor="expenseon">Text</label>
      <input
        className={styles.input}
        id="expenseon"
        type="text"
        placeholder="Expense on What??"
        required
        value={item}
        onChange={(e) => setItem(e.target.value)}
      />

      <div>
        <label htmlFor="expenseAmount">Amount</label>
        <div>(negative - expense,positive-income)</div>
      </div>

      <input
        className={styles.input}
        id="expenseAmount"
        type="number"
        placeholder="Enter amount..."
        required
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
      />

      <button className={styles.tranbtn}>
        {expenseToUpdate ? "Edit " : "Add "}Transaction</button>
    </form>
  )

}

export default ExpenseForm;
